//   paragraph: {
//     textAlign: 'left',
//     fontSize: 14,
//     color: '#545871',
//   },
//   profile: {
//     backgroundColor: '#e8e9ed',
//     alignItems: 'center',
//     justifyContent: 'center',
//   },
//   info: {
//     justifyContent: 'flex-start',
//     fontFamily: 'Roboto_400Regular',
//     flexDirection: 'row',
//     padding: 30,
//     width: 290,
//     borderBottomWidth: 2,
//     borderBottomColor: '#e8e9ed',
//     backgroundColor: '#ffffff',
//   },
//   });

export default AboutAdopting;
