import React from 'react';
import { ImageBackground,Text, StyleSheet, View, TouchableOpacity, ScrollView} from 'react-native';
import { NativeBaseProvider, Image, Button} from "native-base"; 

// import { NavigationContainer } from '@react-navigation/native';
// import { createNativeStackNavigator } from '@react-navigation/native-stack';


import {
  useFonts,
  PaytoneOne_400Regular
} from '@expo-google-fonts/paytone-one'
import {
  Roboto_400Regular,
} from '@expo-google-fonts/roboto'
import AppLoading from 'expo-app-loading';


function adoption({ navigation }) {
  let [fontsLoaded, error] = useFonts({
    Roboto_400Regular,
    PaytoneOne_400Regular
  })
  if (!fontsLoaded) {
    return <AppLoading />
  }
  return (
    
    <ScrollView>
      <NativeBaseProvider>
      
      {/* <NavigationContainer>
        <Stack.Navigator>
          <Stack.Screen name="Home" component={HomeScreen} />
        </Stack.Navigator>
      </NavigationContainer> */}

     
        <View style={styles.buttonbox} > 

          {/* <View style={styles.preadoption}>
            <Text>Information about pre-adoption</Text>
          </View> */}
      
          <Button
            mt="2em"
            padding="1em 3em"
            borderRadius="30px"
            backgroundColor="#E19D98"
            onPress={() => navigation.navigate("Emergency")}
            title="Understanding requirements"
          >
            Understanding requirements
          </Button>

          <Button
            mt="2em"
            padding="1em 6em"
            borderRadius="30px"
            backgroundColor="#E19D98"
            onPress={() => navigation.navigate("")}
            title="What to expect"
          >
            What to expect
          </Button>

          <Button
            mt="2em"
            padding="1em 8em"
            borderRadius="30px"
            backgroundColor="#E19D98"
            onPress={() => navigation.navigate("")}
            title=""
          >
            Quiz
          </Button>

          <View style={styles.prebox}>
            <Text style={styles.heading}>
              How to bond {'\n'}
            </Text>

            <Text style={styles.paragraph}> 
              text for bonding 
                


            </Text>

            <Text style={styles.heading}>
              Advanced training 
            </Text>
            <Text style={styles.paragraph}> 
              If you need advanced training assistance, please use the following numbers to call for help or drive to your nearest training centre:
              {'\n'}
             
              
            </Text>


          </View>


          {/* <View style={styles.postbox}>
            <Text>Information about post-adoption</Text>
          </View> */}
          <Button
            mt="2em"
            padding="1em 5.5em"
            borderRadius="30px"
            backgroundColor="#545871"
            onPress={() => navigation.navigate("")}
            title="Emergencies"
          >
            Emergency help
          </Button>

          <View style={styles.postbox}>
            <Text style={styles.heading}>
              When to seek emergency help {'\n'}
            </Text>

            <Text style={styles.paragraph}> 
              If you have any emergencies or major concerns with your new pet, you should consider taking it to an emergency pet centre. It is important to distinguish between the need for an emergency appointment and a regular vet booking to avoid overcrowding the emergency centres. If your pet is displaying any of the following symptoms, please phone your nearest emergency centre:
              {'\n'}
              {'\n'}
              {'\u2B24'} Significant injury - hit by a car, falling from a considerable height, etc.
              {'\n'}
              {'\u2B24'} Vomiting, coughing, diarrhea


            </Text>

            <Text style={styles.heading}>
              Nearby facilities
            </Text>
            <Text style={styles.paragraph}> 
              If you need emergency assistance, please use the following numbers to call for help or drive to your nearest animal hospital:
              {'\n'}
              {'\u2B24'}3379 6408, Sherwood veterinary Practice 
              {'\n'}
              {'\u2B24'}(07) 3062 7686, Animal Emergency Service
              {'\n'}
              
            </Text>


          </View>
            


          <Button
            mt="2em"
            padding="1em 8em"
            borderRadius="30px"
            backgroundColor="#545871"
            onPress={() => navigation.navigate("")}
            title="How to train"
          >
            How to train
          </Button>

          <View style={styles.postbox}>
            <Text style={styles.heading}>
              How to bond {'\n'}
            </Text>

            <Text style={styles.paragraph}> 
              text for bonding 
              {'\n'}
              {'\n'}
              {'\u2B24'} Significant injury - hit by a car, falling from a considerable height, etc.
              {'\n'}
              {'\u2B24'} Vomiting, coughing, diarrhea


            </Text>

            <Text style={styles.heading}>
              Advanced training 
            </Text>
            <Text style={styles.paragraph}> 
              If you need advanced training assistance, please use the following numbers to call for help or drive to your nearest training centre:
              {'\n'}
              {'\u2B24'}  +61 738 991 899, DOGUE Bulimba
              {'\n'}
              {'\u2B24'} +61 434 067 536, Bonnies Dog Obedience & Puppy School
              {'\n'}
              
            </Text>


          </View>



          <Button
            mt="2em"
            padding="1em 6em"
            borderRadius="30px"
            backgroundColor="#545871"
            onPress={() => navigation.navigate("")}
            title="Medical help"
          >
            Medical help
          </Button>
          <Button
            mt="2em"
            padding="1em 4.5em"
            borderRadius="30px"
            backgroundColor="#545871"
            onPress={() => navigation.navigate("")}
            title="Community support"
          >
            Community support
          </Button>
        </View>


        
    </NativeBaseProvider>
    </ScrollView>
  );
}

const AboutAdopting = () => {
  //const Tab = createBottomTabNavigator();
  const Stack = createStackNavigator();
  return (
    <NavigationContainer>
      <Stack.Navigator>
        
        <Stack.Screen name="Signup" options={{ headerShown: false }} component={SignUpScreen}  /> 
        <Stack.Screen name="Login" options={{ headerShown: false }} component={LogInScreen} /> 
        <Stack.Screen name="Emergency" component={Emergencies} /> 
        {/* <LogInScreen navigation={this.props.navigation} /> */}
      </Stack.Navigator>
    </NavigationContainer>
  );
}

const styles = StyleSheet.create({
  heading: {
    textAlign: "center",
    fontFamily: "PaytoneOne_400Regular",
    color: "white",
    paddingTop: 15,
    fontSize: 16,
    marginBottom: 15,
  },
  paragraph: {
    textAlign: "left",
    fontFamily: 'Roboto_400Regular',
    fontSize: 14,
    color: "#545871",
  },
  box: {
    paddingTop: 20,
    paddingBottom: 20,
    paddingLeft: 20,
    paddingRight: 20,
    justifyContent: 'center',
    backgroundColor: '#bbadc6',
    width: 'auto',
    
  },
  prebox: {
    paddingTop: 20,
    paddingBottom: 20,
    paddingLeft: 20,
    paddingRight: 20,
    justifyContent: 'center',
    backgroundColor: '#FBDADB',
    // backgroundColor: '#FBE2E1',
    width: 'auto',
    
  },
  postbox: {
    paddingTop: 20,
    paddingBottom: 20,
    paddingLeft: 20,
    paddingRight: 20,
    justifyContent: 'center',
    //backgroundColor: '#535971',
    backgroundColor: '#bbadc6',
    width: 'auto',
    
  },
  preadoption: {
    paddingTop: 20,
    paddingBottom: 20,
    paddingLeft: 20,
    paddingRight: 20,
    justifyContent: 'center',
    backgroundColor: '#E19D98',
    width: 'auto',
    
  },
  postadoption: {
    paddingTop: 20,
    paddingBottom: 20,
    paddingLeft: 20,
    paddingRight: 20,
    justifyContent: 'center',
    backgroundColor: '#535971',
    width: 'auto',
    
  },
  container: {
    textAlign: 'center',
  },
  
  buttonbox: {
    // backgroundColor: '#ffffff',
    alignItems: 'center',
    justifyContent: 'flex-start',
  },
});



export default AboutAdopting;
