declare const injectedScript = "\n(function () {\n  var RNWV = window.ReactNativeWebView;\n\n  // Send size on body content height updates\n  function postSize() {\n    var tables = document.getElementsByTagName('table');\n    if (tables.length > 0) {\n      var table = tables[0];\n      var tableHeight = table.scrollHeight + Number(table.border) * 2;\n      RNWV.postMessage(JSON.stringify({\n        type: 'heightUpdate',\n        content: tableHeight\n      }));\n    }\n  }\n  postSize();\n  //trigger when DOM changes\n  var MutationObserver = window.MutationObserver || window.WebKitMutationObserver;\n  var observer = new MutationObserver(postSize);\n  observer.observe(document, {\n      subtree: true,\n      attributes: true\n  });\n\n  // Intercept click events\n\n  function findParent(tagname,el){\n    while (el){\n        if ((el.nodeName || el.tagName).toLowerCase() === tagname.toLowerCase()){\n            return el;\n        }\n        el = el.parentNode;\n    }\n    return null;\n  }\n\n  function interceptClickEvent(e) {\n    var href;\n    var target = e.target || e.srcElement;\n    let anchor = findParent('a', target)\n    if (anchor) {\n        href = anchor.getAttribute('href');\n        e.preventDefault();\n        // Post message\n        RNWV.postMessage(JSON.stringify({\n          type: 'navigateEvent',\n          content: href\n        }));\n    }\n  }\n\n  document.addEventListener('click', interceptClickEvent);\n})();\n";
export default injectedScript;
